CREATE OR REPLACE SECURE VIEW ODS_GRS_NO.DBO.XPROCESSED_BARCODES_SDV AS    with sub1 as (   SELECT PT2.LOGG_ID, PT2.TRANS_ID, PT2.SUB_ID, PT2.BARCODE_TYPE, RM.BARCODE, REP_MARK_TYPE_ID   FROM PROCESSED_TRANSACTIONS PT2   LEFT OUTER JOIN REPORT_MARK RM   ON   PT2.BARCODE_NO = RM.BARCODE   )   SELECT   PT.*   FROM "PROCESSED_TRANSACTIONS" PT   inner join sub1 s1   on s1.LOGG_ID = PT.LOGG_ID   and s1.TRANS_ID = PT.TRANS_ID   and s1.SUB_ID = PT.SUB_ID   WHERE     (s1.BARCODE_TYPE <> 2) OR     (s1.BARCODE_TYPE = 2) AND       ---- PRECISERING MEN INGA HSNDELSE, STGSRD ETC      (s1.REP_MARK_TYPE_ID BETWEEN 71 AND 80 ----and NOT (PT2.REP_MARK_TYPE_ID BETWEEN 71 AND 80 OR PT2.REP_MARK_TYPE_ID BETWEEN 31 AND 70 OR PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 99)      and not exists (      select 1       from sub1 PT2      WHERE       PT2.LOGG_ID = s1.LOGG_ID      and PT2.TRANS_ID = s1.TRANS_ID      and (PT2.SUB_ID=0 or PT2.SUB_ID =s1.SUB_ID)      and (PT2.REP_MARK_TYPE_ID BETWEEN 71 AND 80 OR PT2.REP_MARK_TYPE_ID BETWEEN 31 AND 70 OR PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 99)      )      )       OR      ---- KOMPONENT MEN INGA ANDRA      (s1.REP_MARK_TYPE_ID BETWEEN 11 AND 30 ----AND NOT (PT2.REP_MARK_TYPE_ID BETWEEN 31 AND 99)       and not exists (      select 1       from sub1 PT2      WHERE       PT2.LOGG_ID = s1.LOGG_ID      and PT2.TRANS_ID = s1.TRANS_ID      and (PT2.SUB_ID=0 or PT2.SUB_ID =s1.SUB_ID)      and PT2.REP_MARK_TYPE_ID BETWEEN 31 AND 99      )      )      OR     ---- HSNDELSE MEN INTE STGSRD ETC      (s1.REP_MARK_TYPE_ID BETWEEN 31 AND 50 ----AND NOT (PT2.REP_MARK_TYPE_ID BETWEEN 51 AND 70 OR PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 99)       and not exists (      select 1       from sub1 PT2      WHERE       PT2.LOGG_ID = s1.LOGG_ID      and PT2.TRANS_ID = s1.TRANS_ID      and (PT2.SUB_ID=0 or PT2.SUB_ID =s1.SUB_ID)             and (PT2.REP_MARK_TYPE_ID BETWEEN 51 AND 70 OR PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 99)      )      )     OR       ---- +TGSRD MEN INTE TGRD,  SIGNATUR ETC      (s1.REP_MARK_TYPE_ID BETWEEN 51 AND 70 ----AND NOT (PT2.REP_MARK_TYPE_ID BETWEEN 51 AND 70 OR PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 99)      and not exists (      select 1       from sub1 PT2      WHERE       PT2.LOGG_ID = s1.LOGG_ID      and PT2.TRANS_ID = s1.TRANS_ID      and (PT2.SUB_ID=0 or PT2.SUB_ID =s1.SUB_ID)             and (PT2.REP_MARK_TYPE_ID BETWEEN 51 AND 70 OR PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 99)       )      )      OR     ---- SIGNATUR MEN INTE TIDSGRSNS      (s1.REP_MARK_TYPE_ID BETWEEN 96 AND 99 ----AND NOT (PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 95)      and not exists (      select 1       from sub1 PT2      WHERE       PT2.LOGG_ID = s1.LOGG_ID      and PT2.TRANS_ID = s1.TRANS_ID      and (PT2.SUB_ID=0 or PT2.SUB_ID =s1.SUB_ID)             and (PT2.REP_MARK_TYPE_ID BETWEEN 91 AND 95)      )       )      OR     ---- TIDSGRSNS      (s1.REP_MARK_TYPE_ID BETWEEN 91 AND 95)    ----limit 10000000   ;
  
    

